mapper.select:
 select  bl.id, bl.action_type, bl.opr_no, bl.refer_id, bl.user_id, bl.trace_id, bl.opr_table_name,
bl.action_datetime,
bl.extend_content,ai.action_name from business_log bl left join action_item ai on bl.action_type = ai.id where refer_id = #{orderNo}  and action_type = #{actionType}
